import { StyleSheet, Text, View, Dimensions, ScrollView, TouchableOpacity} from 'react-native'

const screenWidth: number = Dimensions.get("screen").width
const screenHeight: number = Dimensions.get("screen").height

const CryptoList: React.FC = ({filteredList, setQuery, getPrice, query, setCurrentCrypto, setPrice, setLoading}) =>{

    const select = async(item) => {
        setLoading(true)
      try {
        const response = await axios.get(`${baseURL}/simple/price?ids=${query}&vs_currencies=usd`);
        let data = response.data
        if(Object.keys(data).length !== 0){
          setPrice(Object.keys(data).map(k => data[k]))
          setCurrentCrypto(Object.keys(data)[0])
        }
        setLoading(false)
      } catch (error) {
        console.error(error);
        setLoading(false)
      }
    }

    useEffect(() => {
        effect
        return () => {
            cleanup
        }
    }, [])



    return (
        <View style={styles.container}>
        <ScrollView>
            {filteredList.map((item: any, index: number) => (
