import React, {useEffect} from 'react'
import { StyleSheet, Text, View, Dimensions, ScrollView, TouchableOpacity} from 'react-native'
import axios from 'axios';
import { SetStateAction } from 'react';


const screenWidth: number = Dimensions.get("screen").width
const screenHeight: number = Dimensions.get("screen").height

interface fetchList {
    filteredList: () => object,
    setCurrentCrypto: SetStateAction<any>,
    setPrice: SetStateAction<any>
    setLoading: SetStateAction<any>,
    baseURL: string,
    setOpenList: SetStateAction<any>


}

const CryptoList: React.FC<fetchList> = ({filteredList, setCurrentCrypto, setPrice, setLoading, baseURL, setOpenList}) =>{

    const select = async(item: any) => {
    // const formated = item.name.toLowerCase().replace(" ", "")
    console.log("ITEM", item)
    // console.log("FORA", formated)
    setLoading(true)
    setOpenList(false)
      try {
        const response = await axios.get(`${baseURL}/simple/price?ids=${item.id}&vs_currencies=usd`);
        let data = response.data
        console.log("DATA", data)
        if(Object.keys(data).length !== 0){
          setPrice(Object.keys(data).map(k => data[k]))
          setCurrentCrypto(Object.keys(data)[0])
        }
        setLoading(false)
      } catch (error) {
        console.error(error);
        setLoading(false)
      }
    }




    return (
        <View style={styles.container}>
        <ScrollView>
            {filteredList.map((item: any, index: number) => (
                <TouchableOpacity onPress={() =>Â select(item)}>
                <Text style={styles.item}>{item.name}</Text>
                </TouchableOpacity>
            ))}
        </ScrollView>
        </View>
    )
}

export default CryptoList

const styles = StyleSheet.create({
    container: {
        width: screenWidth * 0.8,
        backgroundColor: "rgba(60, 58, 102, 01)",
        maxHeight: 200,
        marginTop: 5,
        position: "absolute",
        zIndex: 999,
        top: 50

        // overflow: "hidden"
    },
    item: {
        fontSize: 16,
        color: "white",
        // marginBottom: 5,
        margin: 5
    }
})
