import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { StyleSheet, Text, View, Platform, TextInput, SafeAreaView, Dimensions, Keyboard, TouchableOpacity,ScrollView, KeyboardAvoidingView } from 'react-native';
import axios from 'axios';

const screenWidth = Dimensions.get("screen").width
const baseURL = "https://api.coingecko.com/api/v3"



export default function App() {
  const [query, setQuery] = useState<String>("bitcoin")
  const [price, setPrice] = useState<any>()
  console.log("helo", price)

  const getPrice = async() => {
    try {
      const response = await axios.get(`${baseURL}/simple/price?ids=${query}&vs_currencies=usd`);
      let data = response.data
      setPrice(Object.keys(data).map(k => data[k]))
    } catch (error) {
      console.error(error);
    }
  }



  return (
    <>
    <SafeAreaView style={{backgroundColor: '#1a1845', flex: 1}}>
    <KeyboardAvoidingView 
      style={styles.container} 
      behavior={Platform.OS === 'ios' ? 'padding' : null}>
      <View>
        <TextInput 
          style={styles.textInput}
          onChangeText={text => setQuery(text)}
          />
      </View> 
      <View>
      {price &&Â 
      <View style={styles.priceContainer}>
        <Text style={{color: "white", fontSize: 20, marginBottom: 20}}>{query}'s current price is:</Text>
        <Text style={styles.price}>
          ${price[0].usd}
        </Text>
      </View>}

      </View>

      <TouchableOpacity activeOpacity={0.8} disabled={!query.length} onPress={getPrice}>
          <View style={[styles.button, {opacity: !query.length? 0.5 : 1}]}>
              <Text style={styles.buttonText}>Search</Text>
          </View>
        </TouchableOpacity>
      </KeyboardAvoidingView> 
    </SafeAreaView>
    </>
  );
}



const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#1a1845',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  textInput: {
    height: 50,
    width: screenWidth*0.8,
    borderWidth: 0,
    borderBottomWidth: 1,
    borderBottomColor: "rgba(227, 227, 227, 0.7)",
    color: "white",
    fontSize: 20,
    fontWeight: "600",
    paddingHorizontal: 5
  },
  button: {
    width: screenWidth*0.8,
    height: 40,
    borderRadius: 16,
    backgroundColor: "rgba(109, 207, 135, 1)",
    marginTop: 30,
    justifyContent: "center",
    alignItems: "center",
    marginBottom: 10
  },
  buttonText: {
    color: "white",
    fontSize: 20,
    fontWeight: "600"
  },
  priceContainer: {
    justifyContent: "center",
    alignItems: "center"
  },
  price: {
    color: "white",
    fontSize: 30,
    fontWeight: "500"
  }
});
